mapper.insert:
INSERT INTO user
(
      idx
    , identity
    , password
    , remember
    , remember_time
)
VALUES
(
      #{idx}
    , #{identity}
    , #{password}
    , #{remember}
    , #{remember_time}
);

mapper.update:
UPDATE user SET
    identity = #{identity}
  , password = #{password}
  , remember = #{remember}
  , remember_time = #{remember_time}
WHERE idx = #{idx}

UPDATE user SET
    remember = #{remember}
  , remember_time = #{remember_time}
WHERE identity = #{identity}
